import React, { useState, useEffect } from 'react';
import { getStartups } from '../services/startupService';
import { useNavigate } from 'react-router-dom';
import { sendMessage, getMessages } from '../services/messageService';

const AllStartupsPage = ({ currentUser }) => {
  const [startups, setStartups] = useState([]);
  const [error, setError] = useState(null);
  const [messages, setMessages] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchStartups = async () => {
      try {
        const startupsList = await getStartups();
        setStartups(startupsList);
      } catch (err) {
        if (err.response && err.response.status === 401) {
          setError('You are not authorized to view this page. Please log in.');
          navigate('/login'); // Redirect to login page
        } else {
          setError('An error occurred while fetching startups.');
        }
      }
    };

    const fetchMessages = async () => {
      try {
        const messages = await getMessages(currentUser._id);
        setMessages(messages);
      } catch (err) {
        console.error('Error fetching messages:', err);
      }
    };

    fetchStartups();
    fetchMessages();
  }, [navigate, currentUser]);

  const handleSendMessage = async (recipientId, messageText) => {
    try {
      await sendMessage(currentUser.id, recipientId, messageText);
      // Optionally, fetch updated messages after sending
      const updatedMessages = await getMessages(currentUser.id);
      setMessages(updatedMessages);
    } catch (err) {
      console.error('Error sending message:', err);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">All Startups</h1>
      {error ? (
        <p className="text-red-500">{error}</p>
      ) : (
        <ul>
          {startups.map((startup) => (
            <li key={startup._id} className="mb-4">
              <h2 className="text-xl">{startup.name}</h2>
              <p>{startup.description}</p>
              <p>{startup.website}</p>
              <p>{startup.foundingDate}</p>
              {/* Add messaging button */}
              <button onClick={() => handleSendMessage(startup.founderId, "Hello!")}>
                Send Message
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default AllStartupsPage;
