import React, { useState, useEffect } from 'react';
import { getStartups, addStartup, updateStartup, deleteStartup } from '../services/startupService';
import AllStartupsPage from './AllStartupsPage';

const StartupPage = () => {
  const [startups, setStartups] = useState([]);
  const [form, setForm] = useState({ name: '', description: '' });
  const [editing, setEditing] = useState(null);

  useEffect(() => {
    const fetchStartups = async () => {
      const startupsList = await getStartups();
      setStartups(startupsList);
    };
    fetchStartups();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (editing) {
      await updateStartup(editing._id, form);
    } else {
      await addStartup(form);
    }
    setForm({ name: '', description: '' });
    setEditing(null);
    const startupsList = await getStartups();
    setStartups(startupsList);
  };

  const handleEdit = (startup) => {
    setForm({ name: startup.name, description: startup.description });
    setEditing(startup);
  };

  const handleDelete = async (id) => {
    await deleteStartup(id);
    const startupsList = await getStartups();
    setStartups(startupsList);
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Startups</h1>
      <form onSubmit={handleSubmit} className="mb-4">
        <div className="mb-2">
          <input
            type="text"
            name="name"
            value={form.name}
            onChange={handleChange}
            placeholder="Name"
            className="border p-2 w-full"
          />
        </div>
        <div className="mb-2">
          <input
            type="text"
            name="description"
            value={form.description}
            onChange={handleChange}
            placeholder="Description"
            className="border p-2 w-full"
          />
        </div>
        <button type="submit" className="bg-blue-500 text-white p-2 rounded">
          {editing ? 'Update' : 'Add'} Startup
        </button>
      </form>
      <ul>
     <AllStartupsPage/>
      </ul>
    </div>
  );
};

export default StartupPage;
