// BlogPage.jsx

import React, { useState, useEffect } from 'react';
import { getBlogs, addBlog, updateBlog, deleteBlog } from '../services/blogService';

const BlogPage = () => {
  const [blogs, setBlogs] = useState([]);
  const [formData, setFormData] = useState({
    title: '',
    content: ''
  });
  const [editMode, setEditMode] = useState(false);
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    fetchBlogs();
  }, []);

  const fetchBlogs = async () => {
    const blogsList = await getBlogs();
    setBlogs(blogsList);
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (editMode) {
      await updateBlog(editId, formData);
    } else {
      await addBlog(formData);
    }
    fetchBlogs();
    setFormData({ title: '', content: '' });
    setEditMode(false);
    setEditId(null);
  };

  const handleEdit = (blog) => {
    setFormData({ title: blog.title, content: blog.content });
    setEditMode(true);
    setEditId(blog._id);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this blog post?')) {
      await deleteBlog(id);
      fetchBlogs();
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Blog Management</h1>

      {/* Form for adding/updating blogs */}
      <form onSubmit={handleSubmit} className="mb-4">
        <div className="mb-4">
          <label htmlFor="title" className="block text-sm font-medium text-gray-700">
            Title
          </label>
          <input
            type="text"
            id="title"
            name="title"
            value={formData.title}
            onChange={handleInputChange}
            required
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
          />
        </div>
        <div className="mb-4">
          <label htmlFor="content" className="block text-sm font-medium text-gray-700">
            Content
          </label>
          <textarea
            id="content"
            name="content"
            value={formData.content}
            onChange={handleInputChange}
            required
            rows="4"
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
          ></textarea>
        </div>
        <div>
          <button
            type="submit"
            className="bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600 mr-2"
          >
            {editMode ? 'Update Blog Post' : 'Add Blog Post'}
          </button>
          {editMode && (
            <button
              type="button"
              onClick={() => {
                setEditMode(false);
                setEditId(null);
                setFormData({ title: '', content: '' });
              }}
              className="bg-gray-500 text-white py-2 px-4 rounded hover:bg-gray-600"
            >
              Cancel
            </button>
          )}
        </div>
      </form>

      {/* List of blogs */}
      <div>
        {blogs.map((blog) => (
          <div key={blog._id} className="mb-4 p-4 border border-gray-300 rounded-md">
            <h2 className="text-lg font-semibold">{blog.title}</h2>
            <p className="text-sm text-gray-700">{blog.content}</p>
            <div className="mt-2">
              <button
                onClick={() => handleEdit(blog)}
                className="bg-green-500 text-white py-1 px-2 rounded hover:bg-green-600 mr-2"
              >
                Edit
              </button>
              <button
                onClick={() => handleDelete(blog._id)}
                className="bg-red-500 text-white py-1 px-2 rounded hover:bg-red-600"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BlogPage;
