import React, { useContext, useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

const Navbar = () => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const [showStartupsDropdown, setShowStartupsDropdown] = useState(false);
  const [showBlogDropdown, setShowBlogDropdown] = useState(false);
  const [showInvestorsDropdown, setShowInvestorsDropdown] = useState(false);

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  const toggleDropdown = (dropdown) => {
    // Close all dropdowns except the one being toggled
    setShowStartupsDropdown(dropdown === 'startups' ? !showStartupsDropdown : false);
    setShowBlogDropdown(dropdown === 'blogs' ? !showBlogDropdown : false);
    setShowInvestorsDropdown(dropdown === 'investors' ? !showInvestorsDropdown : false);
  };

  return (
    <nav className="bg-gray-800 p-4">
      <div className="container mx-auto flex justify-between items-center">
        <div className="text-white">
          <Link to="/" className="text-lg font-bold">
            Startup Board
          </Link>
        </div>
        <ul className="flex space-x-4">
          {user ? (
            <>
              <li className="relative">
                <Link
                  to="/AllStartups"
                  className="text-white hover:text-gray-300"
                  onClick={() => toggleDropdown('startups')}
                  onMouseEnter={() => setShowStartupsDropdown(true)}
                  onMouseLeave={() => setShowStartupsDropdown(false)}
                >
                  All Startups
                  {showStartupsDropdown && (
                    <ul
                      className="absolute bg-gray-800 text-white rounded mt-1"
                      onMouseEnter={() => setShowStartupsDropdown(true)}
                      onMouseLeave={() => setShowStartupsDropdown(false)}
                    >
                      <li>
                        <Link to="/AllStartups" className="block px-4 py-2 hover:bg-gray-700">
                          All Startups
                        </Link>
                      </li>
                      <li>
                        <Link to="/startup" className="block px-4 py-2 hover:bg-gray-700">
                          Add Startup
                        </Link>
                      </li>
                    </ul>
                  )}
                </Link>
              </li>
              <li className="relative">
                <Link
                  to="/AllBlogs"
                  className="text-white hover:text-gray-300"
                  onClick={() => toggleDropdown('blogs')}
                  onMouseEnter={() => setShowBlogDropdown(true)}
                  onMouseLeave={() => setShowBlogDropdown(false)}
                >
                  Blog Management
                  {showBlogDropdown && (
                    <ul
                      className="absolute bg-gray-800 text-white rounded mt-1"
                      onMouseEnter={() => setShowBlogDropdown(true)}
                      onMouseLeave={() => setShowBlogDropdown(false)}
                    >
                      <li>
                        <Link to="/AllBlogs" className="block px-4 py-2 hover:bg-gray-700">
                          All Blogs
                        </Link>
                      </li>
                      <li>
                        <Link to="/blog" className="block px-4 py-2 hover:bg-gray-700">
                          Add Blog
                        </Link>
                      </li>
                    </ul>
                  )}
                </Link>
              </li>
              <li className="relative">
                <Link
                  to="/AllInvestors"
                  className="text-white hover:text-gray-300"
                  onClick={() => toggleDropdown('investors')}
                  onMouseEnter={() => setShowInvestorsDropdown(true)}
                  onMouseLeave={() => setShowInvestorsDropdown(false)}
                >
                  All Investors
                  {showInvestorsDropdown && (
                    <ul
                      className="absolute bg-gray-800 text-white rounded mt-1"
                      onMouseEnter={() => setShowInvestorsDropdown(true)}
                      onMouseLeave={() => setShowInvestorsDropdown(false)}
                    >
                      <li>
                        <Link to="/AllInvestors" className="block px-4 py-2 hover:bg-gray-700">
                          All Investors
                        </Link>
                      </li>
                      <li>
                        <Link to="/investor" className="block px-4 py-2 hover:bg-gray-700">
                          Add Investor
                        </Link>
                      </li>
                    </ul>
                  )}
                </Link>
              </li>
              <li>
                <Link to="/Profile" className="text-white hover:text-gray-300">
                  Your Profile
                </Link>
              </li>
              <li>
                <button onClick={handleLogout} className="text-white hover:text-gray-300">
                  Logout
                </button>
              </li>
            </>
          ) : (
            <>
              <li>
                <Link to="/" className="text-white hover:text-gray-300">
                  Home
                </Link>
              </li>
              <li>
                <Link to="/Login" className="text-white hover:text-gray-300">
                  Login
                </Link>
              </li>
              <li>
                <Link to="/Register" className="text-white hover:text-gray-300">
                  Register
                </Link>
              </li>
            </>
          )}
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
