import React, { useState, useEffect } from 'react';
import { getInvestors, addInvestor, updateInvestor, deleteInvestor } from '../services/investorService';

const InvestorPage = () => {
  const [investors, setInvestors] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
  });
  const [editMode, setEditMode] = useState(false);
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    fetchInvestors();
  }, []);

  const fetchInvestors = async () => {
    const investorsList = await getInvestors();
    setInvestors(investorsList);
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (editMode) {
      await updateInvestor(editId, formData);
    } else {
      await addInvestor(formData);
    }
    fetchInvestors();
    setFormData({ name: '', description: '', investmentFields:'', investmentFields:'' });
    setEditMode(false);
    setEditId(null);
  };

  const handleEdit = (investor) => {
    setFormData({ name: investor.name, description: investor.description });
    setEditMode(true);
    setEditId(investor._id);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this investor?')) {
      await deleteInvestor(id);
      fetchInvestors();
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold">Investors</h1>

      {/* Form for adding/updating investors */}
      <form onSubmit={handleSubmit} className="mb-4">
        <div className="mb-4">
          <label htmlFor="name" className="block text-sm font-medium text-gray-700">
            Name
          </label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleInputChange}
            required
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
          />
        </div>
        <div className="mb-4">
          <label htmlFor="description" className="block text-sm font-medium text-gray-700">
            Description
          </label>
          <textarea
            id="description"
            name="description"
            value={formData.description}
            onChange={handleInputChange}
            required
            rows="3"
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
          ></textarea>
        </div>
        <div>
          <button
            type="submit"
            className="bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600 mr-2"
          >
            {editMode ? 'Update Investor' : 'Add Investor'}
          </button>
          {editMode && (
            <button
              type="button"
              onClick={() => {
                setEditMode(false);
                setEditId(null);
                setFormData({ name: '', description: '' });
              }}
              className="bg-gray-500 text-white py-2 px-4 rounded hover:bg-gray-600"
            >
              Cancel
            </button>
          )}
        </div>
      </form>

      {/* List of investors */}
      <div>
        {investors.map((investor) => (
          <div key={investor._id} className="mb-4 p-4 border border-gray-300 rounded-md">
            <h2 className="text-lg font-semibold">{investor.name}</h2>
            <p className="text-sm text-gray-700">{investor.description}</p>
            <div className="mt-2">
              <button
                onClick={() => handleEdit(investor)}
                className="bg-green-500 text-white py-1 px-2 rounded hover:bg-green-600 mr-2"
              >
                Edit
              </button>
              <button
                onClick={() => handleDelete(investor._id)}
                className="bg-red-500 text-white py-1 px-2 rounded hover:bg-red-600"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default InvestorPage;
