// login.js
import { useState, useContext } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

const LoginPage = () => {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [user, setUser] = useState(null);
  const navigate = useNavigate();
  const { login } = useContext(AuthContext);



  

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`http://localhost:5000/users/login`, formData);
      console.log('User logged in:', response.data);

      // Assuming server sends back a JWT token in response.data.token
      const token = response.data.token;

      // Use the login function from AuthContext to handle token and user state
      await login(token);
      setUser(response.data);

      // Navigate to /dashboard
      switch (user.profession) {
        case 'founder':
          return navigate('/AllStartups');
         
        case 'investor':
          return navigate('/AllInvestors');
           
        case 'visitor':
          return <div>
            <h2>Visitor Dashboard</h2>
            
            </div>;
        default:
          return <div>Unknown Role</div>;
      }
    } catch (error) {
      console.error('Error logging in:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="email"
        name="email"
        placeholder="Email"
        value={formData.email}
        onChange={handleChange}
      />
      <input
        type="password"
        name="password"
        placeholder="Password"
        value={formData.password}
        onChange={handleChange}
      />
      <button type="submit">Login</button>
    </form>
  );
};

export default LoginPage;
