// profilepage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ProfilePage = () => {
  const [user, setUser] = useState(null);
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    profession: '',
    profilePicture: null,
  });

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await axios.get('http://localhost:5000/users/me', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        setUser(response.data);
        setFormData({
          name: response.data.name,
          email: response.data.email,
          profession: response.data.profession,
          profilePicture: null,
        });
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUser();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleFileChange = (e) => {
    setFormData((prevData) => ({ ...prevData, profilePicture: e.target.files[0] }));
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    const updatedData = new FormData();
    updatedData.append('name', formData.name);
    updatedData.append('email', formData.email);
    updatedData.append('profession', formData.profession);
    if (formData.profilePicture) {
      updatedData.append('profilePicture', formData.profilePicture);
    }

    try {
      const response = await axios.patch('http://localhost:5000/users/me', updatedData, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'multipart/form-data',
        },
      });
      setUser(response.data);
      setEditing(false);
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div className='product-card w-[300px] rounded-md shadow-xl overflow-hidden z-[100] relative cursor-pointer snap-start shrink-0 py-8 px-6 bg-white flex flex-col items-center justify-center gap-3 transition-all duration-300 group'>
       <div
    class="absolute -left-[40%] top-0 group-hover:rotate-12 transition-all duration-300 group-hover:scale-150">
      <div class="flex gap-1">
      <svg
        stroke-linejoin="round"
        stroke-linecap="round"
        stroke-width="1"
        fill="none"
        viewBox="0 0 24 24"
        class="fill-gray-300 rotate-[24deg]"
        height="200"
        width="200"
        xmlns="http://www.w3.org/2000/svg"
      >
        <polygon
          points="12 2 15.09 8.26 22 9.27 17 14.14 18.18 21.02 12 17.77 5.82 21.02 7 14.14 2 9.27 8.91 8.26 12 2"
        ></polygon>
      </svg>
    </div>
  </div>
  <div
    class="absolute rounded-full bg-gray-500 z-20 left-1/2 top-[44%] h-[110%] w-[110%] -translate-x-1/2 group-hover:top-[58%] transition-all duration-300"
  ></div>
  <div class="para uppercase text-center leading-none z-40"></div>
      <p>{user.name}'s Profile</p>
      <p>Email: {user.email}</p>
      <p>Role: {user.profession}</p>
      {user.profilePicture && (
        <img src={user.profilePicture} alt="Profile" style={{ width: '150px', height: '150px' }} />
      )}
      <button onClick={() => setEditing(!editing)}>
        {editing ? 'Cancel' : 'Edit Profile'}
      </button>
      {editing && (
        <form onSubmit={handleFormSubmit}>
          <div>
            <label>Name:</label>
            <input type="text" name="name" value={formData.name} onChange={handleInputChange} />
          </div>
          <div>
            <label>Email:</label>
            <input type="email" name="email" value={formData.email} onChange={handleInputChange} />
          </div>
          <div>
            <label>Profession:</label>
            <select name="profession" value={formData.profession} onChange={handleInputChange}>
              <option value="founder">Founder</option>
              <option value="investor">Investor</option>
              <option value="visitor">Visitor</option>
            </select>
          </div>
          <div>
            <label>Profile Picture:</label>
            <input type="file" name="profilePicture" onChange={handleFileChange} />
          </div>
          <button type="submit">Update Profile</button>
        </form>
      )}
    </div>
  );
};

export default ProfilePage;
