// profilepage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ProfilePage = () => {
  const [user, setUser] = useState(null);
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    profession: 'founder',
    profilePicture: null,
  });

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await axios.get('http://localhost:5000/users/me', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        setUser(response.data);
        setFormData({
          name: response.data.name,
          email: response.data.email,
          profession: response.data.profession,
          profilePicture: null,
        });
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUser();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleFileChange = (e) => {
    setFormData((prevData) => ({ ...prevData, profilePicture: e.target.files[0] }));
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    const updatedData = new FormData();
    updatedData.append('name', formData.name);
    updatedData.append('email', formData.email);
    updatedData.append('profession', formData.profession);
    if (formData.profilePicture) {
      updatedData.append('profilePicture', formData.profilePicture);
    }

    try {
      const response = await axios.patch('http://localhost:5000/users/me', updatedData, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'multipart/form-data',
        },
      });
      setUser(response.data);
      setEditing(false);
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div>
    <div className='m-2 group px-10 py-5 bg-white/10 rounded-lg flex flex-col items-center justify-center gap-2 relative after:absolute after:h-full after:bg-[#abd373] z-20 shadow-lg after:-z-20 after:w-full after:inset-0 after:rounded-lg transition-all duration-300 hover:transition-all hover:duration-300 after:transition-all after:duration-500 after:hover:transition-all after:hover:duration-500 overflow-hidden cursor-pointer after:-translate-y-full after:hover:translate-y-0 [&amp;_p]:delay-200 [&amp;_p]:transition-all'>

{user.profilePicture && (
        <img src={user.profilePicture} alt="Profile" style={{ width: '150px', height: '150px' }} className='w-44 card1img aspect-square text-[#abd373] group-hover:bg-gray-800 text-5xl rounded-full p-2 transition-all duration-300 group-hover:transition-all group-hover:duration-300 group-hover:-translate-y-2 mx-auto' />
      )}
      <p className='cardtxt font-semibold text-gray-200 tracking-wider group-hover:text-gray-700 text-xl'>{user.name}'s Profile</p>

      <p className='blueberry font-semibold text-gray-600 text-xs'> {user.profession}</p>
      <div class="ordernow flex flex-row justify-between items-center w-full">
      <p className='ordernow-text text-[#abd373] font-semibold group-hover:text-gray-800'>{user.email}</p>
     
      <button onClick={() => setEditing(!editing)} className='btun4 lg:inline-flex items-center gap-3 group-hover:bg-white/10 bg-[#abd373] shadow-[10px_10px_150px_#ff9f0d] cursor-pointer py-2 px-4 text-sm font-semibold rounded-full butn'>
        {editing ? 'Cancel' : 'Edit Profile'}
      </button>
      </div>
      </div>

      <div className='flex justify-center'>

      {editing && (
        <form onSubmit={handleFormSubmit} className='mt-5 '>
                <h1 className="text-xl md:text-2xl font-bold italic mb-4 ">Update Profile's</h1>
          <div className='mb-3'>
            <label className='italic font-medium'>Name: </label>
            <input type="text" name="name" value={formData.name} onChange={handleInputChange} className='border p-2  bg-zinc-100 text-black italic font-semibold text-lg font-mono ring-1 ring-zinc-400 focus:ring-2 focus:ring-blue-500 outline-none duration-300 placeholder:text-black placeholder:opacity-100 rounded-full px-4 py-1 shadow-md focus:shadow-lg focus:shadow-blue-50'/>
          </div>
          <div>
            <label className='italic font-medium'>Email: </label>
            <input type="email" name="email" value={formData.email} onChange={handleInputChange} />
          </div>
          <div>
            <label className='italic font-medium'>Profession: </label>
            <select name="profession" value={formData.profession} onChange={handleInputChange}>
              <option value="founder">Founder</option>
              <option value="investor">Investor</option>
              
            </select>
          </div>
          <div>
            <label className='italic font-medium'>Profile Picture: </label>
            <input type="file" name="profilePicture" onChange={handleFileChange} />
          </div>
          <button type="submit">Update Profile</button>
        </form>
        
      )}
      </div>
   
    </div>
  );
};

export default ProfilePage;
