// register.js
import React,{ useState } from 'react';
import axios from 'axios';

const RegisterPage = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    profession: 'visitor',
    profilePicture: null,
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, profilePicture: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append('name', formData.name);
    data.append('email', formData.email);
    data.append('password', formData.password);
    data.append('profession', formData.profession);
    if (formData.profilePicture) {
      data.append('profilePicture', formData.profilePicture);
    }

    try {
      const response = await axios.post(`http://localhost:5000/users/register`, data);
      console.log('User registered:', response.data);

      // Assuming server sends back a JWT token in response.data.token
      const token = response.data.token;

      // Store the token in local storage
      localStorage.setItem('token', token);

      // Optionally, you may redirect to another page or perform other actions after successful registration
      // Example: history.push('/dashboard');
    } catch (error) {
      console.error('Error registering user:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="name"
        placeholder="Name"
        value={formData.name}
        onChange={handleChange}
      />
      <input
        type="email"
        name="email"
        placeholder="Email"
        value={formData.email}
        onChange={handleChange}
      />
      <input
        type="password"
        name="password"
        placeholder="Password"
        value={formData.password}
        onChange={handleChange}
      />
      <select name="profession" value={formData.profession} onChange={handleChange}>
        <option value="founder">Founder</option>
        <option value="investor">Investor</option>
        <option value="visitor">Visitor</option>
      </select>
      <input type="file" name="profilePicture" onChange={handleFileChange} />
      <button type="submit">Register</button>
    </form>
  );
};

export default RegisterPage;
