import React, { useState, useEffect } from 'react';
import { getBlogs, likeBlog } from '../services/blogService';

const AllBlogsPage = () => {
  const [blogs, setBlogs] = useState([]);

  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        const blogsList = await getBlogs();
        setBlogs(blogsList);
      } catch (error) {
        console.error('Error fetching blogs:', error);
      }
    };
    fetchBlogs();
  }, []);

  const handleLike = async (blogId) => {
    try {
      const updatedBlog = await likeBlog(blogId);
      setBlogs(prevBlogs =>
        prevBlogs.map(blog =>
          blog._id === blogId ? { ...blog, likes: updatedBlog.likes } : blog
        )
      );
    } catch (error) {
      console.error('Error liking blog:', error);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">All Blogs</h1>
      <ul>
        {blogs.map((blog) => (
          <li key={blog._id} className="mb-4">
            <h2 className="text-xl">{blog.title}</h2>
            <p>{blog.content}</p>
            <p>Author: {blog.author.name}</p>
            <p>Created At: {new Date(blog.createdAt).toLocaleDateString()}</p>
            <div className="flex items-center mt-2">
              <button
                className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
                onClick={() => handleLike(blog._id)}
              >
                Like
              </button>
              <span className="ml-2">{blog.likes} Likes</span>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AllBlogsPage;
