import React, { useState, useEffect } from 'react';
import { getStartups } from '../services/startupService';
import { useNavigate } from 'react-router-dom';

const AllStartupsPage = () => {
  const [startups, setStartups] = useState([]);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchStartups = async () => {
      try {
        const startupsList = await getStartups();
        setStartups(startupsList);
      } catch (err) {
        if (err.response && err.response.status === 401) {
          setError('You are not authorized to view this page. Please log in.');
          navigate('/login'); // Redirect to login page
        } else {
          setError('An error occurred while fetching startups.');
        }
      }
    };
    fetchStartups();
  }, [navigate]);

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-5xl italic font-bold mb-4 flex justify-center">All Startups</h1>
      <div role="alert" className='text-2xl bg-cyan-100 dark:bg-cyan-800 border-l-4 border-cyan-500 dark:border-cyan-700 text-green-900 dark:text-white p-2 rounded-lg flex items-center transition duration-300 ease-in-out hover:bg-slate-200 dark:hover:bg-slate-800 transform hover:scale-105'>
      {error ? (
        <p className="text-red-500">{error}</p>
      ) : (
        <ul>
          {startups.map((startup) => (
            <li key={startup._id} className="mb-2">
             <h2 className="text-3xl"><span className=' font-semibold text-fuchsia-200'>Company Name:</span> {startup.name}</h2>
              <p> {startup.description}</p>
              <p>{startup.website}</p>
              <p>{startup.foundingDate}</p>
              <p>{startup.email}</p>
            </li>
          ))}
        </ul>
      )}
      </div>
    </div>
  );
};

export default AllStartupsPage;
