// LoginPage.js
import React, { useState, useContext } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

const LoginPage = () => {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const { login } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`http://localhost:5000/users/login`, formData);
      console.log('User logged in:', response.data);

      // Assuming server sends back a JWT token in response.data.token
      const token = response.data.token;

      // Use the login function from AuthContext to handle token and user state
      await login(token);

      // Redirect user based on their role
      console.log('Role received:', response.profession
      ); // Log the role received from the server
      switch (response.data.role.toLowerCase()) { // Ensure lowercase comparison for consistent checks
        case 'founder':
          navigate('/AllStartups');
          break;
        case 'investor':
          navigate('/AllInvestors');
          break;
        case 'visitor':
          navigate('/VisitorDashboard');
          break;
        default:
          console.error('Unknown Role:', response.data.role); // Log the unknown role for further investigation
      }
      
    } catch (error) {
      console.error('Error logging in:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="email"
        name="email"
        placeholder="Email"
        value={formData.email}
        onChange={handleChange}
        required
      />
      <input
        type="password"
        name="password"
        placeholder="Password"
        value={formData.password}
        onChange={handleChange}
        required
      />
      <button type="submit">Login</button>
    </form>
  );
};

export default LoginPage;
