import axios from 'axios';

const sendMessage = async (senderId, recipientId, messageText) => {
  try {
    const response = await axios.post('/messages', {
      sender: senderId,
      recipient: recipientId,
      text: messageText
    });
    return response.data; // Assuming the backend responds with the created message object
  } catch (error) {
    throw error;
  }
};

const getMessages = async (userId) => {
  try {
    const response = await axios.get(`/messages/${userId}`);
    return response.data; // Assuming the backend responds with an array of messages
  } catch (error) {
    throw error;
  }
};

export { sendMessage, getMessages };
