// pages/ALLInvestorsPage.js
import React, { useState, useEffect } from 'react';
import { getInvestors } from '../services/investorService';
import { useNavigate } from 'react-router-dom';

const AllInvestorsPage = () => {
  const [investors, setInvestors] = useState([]);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchInvestorsData = async () => {
      try {
        const investorsList = await getInvestors();
        setInvestors(investorsList);
      } catch (err) {
        if (err.response && err.response.status === 401) {
          setError('You are not authorized to view this page. Please log in.');
          navigate('/login'); // Redirect to login page
        } else {
          setError('An error occurred while fetching startups.');
        }
      }
    };
    fetchInvestorsData();
  }, []);

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">All Investors</h1>
      <ul>
        {investors.map((investor) => (
          <li key={investor._id} className="mb-2">
            <h2 className="text-xl">{investor.name}</h2>
            <p>{investor.description}</p>
            <p>{investor.investmentFields}</p>
            <p>{investor.investmentAmount}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AllInvestorsPage;
