import React, { useState, useEffect } from 'react';
import { getStartups, addStartup, updateStartup, deleteStartup } from '../services/startupService';

const StartupPage = () => {
  const [startups, setStartups] = useState([]);
  const [form, setForm] = useState({ name: '', description: '', website: '', foundingDate: '', email:'' });
  const [editing, setEditing] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchStartups = async () => {
      try {
        const startupsList = await getStartups();
        setStartups(startupsList);
      } catch (error) {
        setError('Failed to fetch startups');
        console.error('Failed to fetch startups:', error);
      }
    };
    fetchStartups();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null); // Reset error state
    try {
      if (editing) {
        await updateStartup(editing._id, form);
      } else {
        await addStartup(form);
      }
      setForm({ name: '', description: '', website: '', foundingDate: '', email:'' });
      setEditing(null);
      const startupsList = await getStartups();
      setStartups(startupsList);
    } catch (error) {
      setError(editing ? 'Failed to update startup/ Investors are not allowed' : 'Failed to add startup');
      console.error(error);
    }
  };

  const handleEdit = (startup) => {
    setForm({ name: startup.name, description: startup.description, website: startup.website, foundingDate: startup.foundingDate, email: startup.email });
    setEditing(startup);
  };

  const handleDelete = async (id) => {
    setError(null); // Reset error state
    try {
      await deleteStartup(id);
      const startupsList = await getStartups();
      setStartups(startupsList);
    } catch (error) {
      setError('Failed to delete startup');
      console.error('Failed to delete startup:', error);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Startups</h1>
      {error && <p className="text-red-500 mb-4">{error}</p>}
      <form onSubmit={handleSubmit} className="mb-4 bg-black w-80 p-5 h-60 shadow-md relative rounded-md">
        <div className="mb-2">
          <input
            type="text"
            name="name"
            value={form.name}
            onChange={handleChange}
            placeholder="Name"
            className="border p-2 w-full bg-zinc-100 text-zinc-600 font-mono ring-1 ring-zinc-400 focus:ring-2 focus:ring-rose-400 outline-none duration-300 placeholder:text-zinc-600 placeholder:opacity-50 rounded-full px-4 py-1 shadow-md focus:shadow-lg focus:shadow-rose-400"
          />
        </div>
        <div className="mb-2">
          <input
            type="text"
            name="description"
            value={form.description}
            onChange={handleChange}
            placeholder="Description"
            className="border p-2 w-full"
          />
        </div>
        <div className="mb-2">
          <input
            type="text"
            name="website"
            value={form.website}
            onChange={handleChange}
            placeholder="Website"
            className="border p-2 w-full"
          />
        </div>
        <div className="mb-2">
          <input
            type="date"
            name="foundingDate"
            value={form.foundingDate}
            onChange={handleChange}
            placeholder="Founding Date"
            className="border p-2 w-full"
          />
        </div>
        <div className="mb-2">
          <input
            type="text"
            name="email"
            value={form.email}
            onChange={handleChange}
            placeholder="Email"
            className="border p-2 w-full"
          />
        </div>
        <button type="submit" className="bg-blue-500 text-white p-2 rounded">
          {editing ? 'Update' : 'Add'} Startup
        </button>
      </form>
      <ul>
        {startups.map((startup) => (
          <li key={startup._id} className="mb-2">
            <div className="flex justify-between items-center">
              <div>
                <h2 className="text-xl">{startup.name}</h2>
                <p>{startup.description}</p>
                <p>{startup.website}</p>
                <p>{startup.foundingDate}</p>
              </div>
              <div>
                <button
                  onClick={() => handleEdit(startup)}
                  className="bg-yellow-500 text-white p-2 rounded mr-2"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(startup._id)}
                  className="bg-red-500 text-white p-2 rounded"
                >
                  Delete
                </button>
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default StartupPage;
