import React, { useState, useContext } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

const LoginPage = () => {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const { login } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`http://localhost:5000/users/login`, formData);
      console.log('User logged in:', response.data);

      const token = response.data.token;

      await login(token);

      console.log('Role received:', response.data.user.profession);
      switch (response.data.user.profession) {
        case 'founder':
          navigate('/AllStartups');
          break;
        case 'investor':
          navigate('/AllInvestors');
          break;
        case 'visitor':
          navigate('/VisitorDashboard');
          break;
        default:
          console.error('Unknown Role:', response.data.user.profession);
      }
    } catch (error) {
      console.error('Error logging in:', error);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-purple-400 via-pink-500 to-red-500">
      <div className="relative">
        <div className="absolute -top-2 -left-2 -right-2 -bottom-2 rounded-lg bg-gradient-to-r from-purple-400 via-pink-500 to-red-500 shadow-lg animate-pulse"></div>
        <div className="bg-white p-6 sm:p-8 md:p-16 rounded-lg shadow-2xl w-72 sm:w-80 md:w-96 lg:w-1/3 xl:w-1/2 relative z-10 transform transition duration-500 ease-in-out">
          <h2 className="text-center text-2xl md:text-3xl font-bold mb-5 md:mb-10 text-gray-800">Login</h2>
          <form onSubmit={handleSubmit} className="space-y-4 md:space-y-5">
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={formData.email}
              onChange={handleChange}
              required
              className="w-full h-10 md:h-12 border border-gray-800 px-3 rounded-lg"
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={formData.password}
              onChange={handleChange}
              required
              className="w-full h-10 md:h-12 border border-gray-800 px-3 rounded-lg"
            />
            <button type="submit" className="w-full h-10 md:h-12 bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">Log in</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
