import React, { useState, useEffect } from 'react';
import { getStartups } from '../services/startupService';

const AllStartupsPage = () => {
  const [startups, setStartups] = useState([]);
  const [form, setForm] = useState({ name: '', description: '' });
  const [editing, setEditing] = useState(null);

  useEffect(() => {
    const fetchStartups = async () => {
      const startupsList = await getStartups();
      setStartups(startupsList);
    };
    fetchStartups();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (editing) {
      await updateStartup(editing._id, form);
    } else {
      await addStartup(form);
    }
    setForm({ name: '', description: '' });
    setEditing(null);
    const startupsList = await getStartups();
    setStartups(startupsList);
  };

  const handleEdit = (startup) => {
    setForm({ name: startup.name, description: startup.description });
    setEditing(startup);
  };

  const handleDelete = async (id) => {
    await deleteStartup(id);
    const startupsList = await getStartups();
    setStartups(startupsList);
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">All Startups</h1>
      <ul>
      {startups.map((startup) => (
          <li key={startup._id} className="mb-2">
            <div className="flex justify-between items-center">
              <div>
                <h2 className="text-xl">{startup.name}</h2>
                <p>{startup.description}</p>
              </div>
              <div>
                <button
                  onClick={() => handleEdit(startup)}
                  className="bg-yellow-500 text-white p-2 rounded mr-2"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(startup._id)}
                  className="bg-red-500 text-white p-2 rounded"
                >
                  Delete
                </button>
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AllStartupsPage;
